jQuery.extend(jQuery.validator.messages,{
    required:"Bu alan zorunludur.",
    remote:"Lütfen bu alanı düzeltin.",
    email:"Lütfen geçerli bir e-posta adresi girin.",
    url:"Lütfen geçerli bir URL girin.",
    date:"Lütfen geçerli bir tarih girin.",
    dateISO:"Lütfen geçerli bir tarih girin (ISO).",
    number:"Lütfen geçerli bir sayı girin.",
    digits:"Lütfen sadece rakam girin.",
    equalTo:"Lütfen aynı değeri tekrar girin.",
    maxlength:jQuery.validator.format("Lütfen {0} karakterden daha az girin."),
    minlength:jQuery.validator.format("Lütfen en az {0} karakter girin."),
    rangelength:jQuery.validator.format("Lütfen {0} ile {1} arasında karakter girin."),
    range:jQuery.validator.format("Lütfen {0} ile {1} arasında bir değer girin."),
    max:jQuery.validator.format("Lütfen {0} değerinden küçük veya eşit bir değer girin."),
    min:jQuery.validator.format("Lütfen {0} değerinden büyük veya eşit bir değer girin.")
});

// Özel alan adları için Türkçe mesajlar
jQuery.validator.addMethod("required", function(value, element) {
    var fieldName = $(element).attr('name');
    var customMessage = "";
    
    switch(fieldName) {
        case "Name":
            customMessage = "Kurs adı boş bırakılamaz.";
            break;
        case "Description":
            customMessage = "Açıklama boş bırakılamaz.";
            break;
        case "ThumbnailUrl":
            customMessage = "Kapak görseli boş bırakılamaz.";
            break;
        case "CategoryId":
            customMessage = "Kategori seçilmelidir.";
            break;
        case "CourseLevelId":
            customMessage = "Seviye seçilmelidir.";
            break;
        case "InstructorId":
            customMessage = "Eğitmen seçilmelidir.";
            break;
        case "Title":
            customMessage = "Kazanım başlığı boş bırakılamaz.";
            break;
        case "CourseId":
            customMessage = "Kurs seçilmelidir.";
            break;
        case "NameSurname":
            customMessage = "Ad Soyad boş bırakılamaz.";
            break;
        case "Position":
            customMessage = "Pozisyon boş bırakılamaz.";
            break;
        case "ImageUrl":
            customMessage = "Resim URL boş bırakılamaz.";
            break;
        default:
            customMessage = "Bu alan zorunludur.";
    }
    
    if (!this.optional(element)) {
        $(element).attr('data-msg-required', customMessage);
    }
    
    return this.optional(element) || value.length > 0;
}, function(value, element) {
    return $(element).attr('data-msg-required') || "Bu alan zorunludur.";
}); 